(()=>{handleTouchClick=function(t,e){t.addEventListener("click",e),t.addEventListener("touchstart",e)};homeSweetHomeToMe=function(){let t=document.querySelectorAll(".vols"),e=document.querySelector(".rocky-top-bg");t.forEach(o=>{handleTouchClick(o,()=>{e.classList.add("run"),setTimeout(()=>{e.classList.remove("run")},1e3)})})};makeAnObservation=function(){let t=new IntersectionObserver(e=>{e.forEach(o=>{if(o.isIntersecting){let n=o.target.getAttribute("data-class");o.target.classList.add(n),t.unobserve(o.target)}})});document.querySelectorAll(".observe").forEach(e=>{t.observe(e)})};mobileNavHandler=function(){let t=document.querySelector(".menu--trigger-button"),e=document.querySelector("body"),o=document.querySelector(".nav"),n=Array.from(o.querySelectorAll("a"));n.push(t),n.forEach(r=>{handleTouchClick(r,()=>{e.classList.toggle("navopen");let a=e.classList.contains("navopen");r.setAttribute("aria-expanded",a),o.setAttribute("aria-hidden",!a)})})};document.addEventListener("DOMContentLoaded",function(t){makeAnObservation(),homeSweetHomeToMe(),mobileNavHandler()});})();
